import Form from '../Form/Form.js';
import Button from '../Button/Button.js';
import './login.css';
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';


function Login() {

    const input_field =
    {
        name: '',
        email: '',
        password: '',


    };
    const form_field =
    {
        name: '',
        id: '',
        type: ''



    }
    var id = useParams();
    console.log(id);

    function handle_submit({ FormData, onSubmit }) {
        event.preventDefault();
        const formData = new FormData(event.target);
        const data = Object.fromEntries(formData.entries());
        console.log(data);
        //add fetch to send data to the server here
        //add a meand to distinguish between login and register fetch calls
        if (id = '/Login') {
            fetch('http://localhost:5000/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', },
                body: JSON.stringify
                    (
                        {
                            name: data.name,
                            email: data.email,

                        }

                    ),
            })
                .then(response => response.json())
                .then(data => console.log(data));
        } else {
            event.preventDefault();
            const formData = new FormData(event.target);
            const data = Object.fromEntries(formData.entries());
            console.log(data);
            //add fetch to send data to the server here
            //add a meand to distinguish between login and register fetch calls

            fetch('http://localhost:5000/Register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', },
                body: JSON.stringify
                    (
                        {
                            name: data.name,
                            email: data.email,

                        }

                    ),
            })
                .then(response => response.json())
                .then(data => console.log(data));

        }
    }
    if (id = '/Register') {
        fields = [
            { name: 'name', id: 'name', type: 'text', value: input_field.name },
            { name: 'email', id: 'email', type: 'email', value: input_field.email },
            { name: 'password', id: 'password', type: 'password', value: input_field.password },

        ];
    }
    else if (id = '/Login') {
        fields = [

            { name: 'email', id: 'email', type: 'email', value: input_field.email },
            { name: 'password', id: 'password', type: 'password', value: input_field.password },

        ];



    }




    return (

        <Form fields />


    )


}



export default Login;
