import React, { useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import './Browse.css';
import Card from '../Components/Card';


function viewProducts(){


    const navigate = useNavigate();

    const url = "http://localhost:5000/product/get-Products";
 
   
    const [products, setProducts] = useState([]);
    const [LoadingProducts, setLoadingProducts] = useState(true); const [searchValues,setSearchValues]=useState(null)

    useEffect(() => {
        try {
            fetch(url).then(response => response.json()).then(data => setProducts(data))
        }
        catch (error) {
            console.error('Error fetching products:', error);
            setLoadingProducts(false);
        }
        finally {
            setLoadingProducts(false)
        }
    }, [url]);

   
        const Search=async (e)=>
        {
            e.preventDefault()
            try{
                let urlS;
                if(!searchValues || !searchValues.search || searchValues.search.trim() === '')
                {
                  urlS=url;
                }else
                {
                  urlS=`http://localhost:5000/product/search/${searchValues.search}`;
                }
            const response= await fetch(urlS,{credentials:'include'});
            if(!response.ok)
            {
                 throw new Error("error getting data");

            }
            const result= await response.json();
          
            setProducts(result);
            

            }
            catch(err)
            {
                console.log(err.message);
               
            }
            
           

        }
    

  const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSearchValues(prevValues => ({
            ...prevValues,
            [name]: value,
        }))
    };

    return (

        <div className="browse-container">
            <h1>Browse Products</h1>
            {
               
                (products.length) ? (
                    <div>
                        <form onSubmit={Search} >
                            <input type='text' name='search' onChange={handleInputChange}></input>
                            <button  type='submit'>Search</button>
                        </form>
                    <div className="product-list">
                        {

                            products.map((data) =>
                                <Card data={data} />
                            )
                        }
                    </div>


                    </div>
                ) :
                    (<div>

                        <p>No products available</p>
                    </div>
                    )

            }

        </div>

    )


}


export default viewProducts;
