    return (
        loadedCart ? (
            <div>
                {
                    cartData.cart.items.map((item) =>
                    (
                        <div>
                        <div>
                            <a href={`images${item.ProductID}`}>{item.name}</a>
                            <p>{item.price}</p>
                            <p>{item.quantity}</p>
                        </div>
                        <button type='button' onClick={checkout}> checkout</button>
                        </div>

                    ))



                } </div>


        ) : (
            <div>
                <h1>Loading cart data ....</h1>

            </div>



        )


    );



};

