import React, { useState, useEffect } from 'react';
import './Browse.css';
import Card from '../Components/Card';
function Browse() {
    const url = `http://localhost:5000/product`;
    const sessionUrl= `http://localhost:5000/user/me`
    const [products, setProducts] = useState([]);
    const [sessionData, setSessionData] = useState(null);
    useEffect(() => {
        fetch(url).then(response => response.json()).then(data => setProducts(data));
    }, []);
    useEffect(() => {
        fetch(sessionUrl, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => {
       
        if (response.ok) { // Check for 2xx status codes
          const data = response.json();
          // Your backend might send { loggedIn: true } or user data
          setIsSessionActive(data.loggedIn || true); // Adjust based on your backend response
        } else if (response.status === 401 || response.status === 403) {
          console.log("Session not active/expired.");
          setIsSessionActive(false);
          // You might want to redirect to login here
        }
        })
        .then(data => console.log(data))
        .catch(error => console.error('Error fetching session data:', error));
    }, []);
    
    
    return (
        <div className="browse-container">
            <h1>Browse Products</h1>
            {
                (products.length) ? (
                    <div className="product-list">
                        {
                           
                            products.map((data) =>
                                <Card data={data} /> 
                            )
                        }


                    </div>
                ) :
                    (<div>

                        <p>No products available</p>
                    </div>
                    )



            }

        </div>

    )


}

export default Browse;
