import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useNavigate } from "react-router-dom";




function Profile({ userData }) {
    const { name, email, role, phoneNumber,_id } = userData;
    const[formValues,setFormValues]=useState(null);
    const url="http://localhost:5000/user/update"
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        console.log(`Input changed: Name=${name}, Value=${value}`);
        setFormValues(prevValues => ({
            ...prevValues,
            [name]: value,
        }))
    };
 

    const update =async (e) => 
   {
    const toUpdate=
    {   id:_id,
        name:formValues.name,
      email:formValues.email,
      role:formValues.role,
      phoneNumber:formValues.phoneNumber,
      oldPassword:formValues.oldPassword,
      newPassword:formValues.newPassword

    }

    try{
        e.preventDefaultSubmit();
        const result=await fetch(url,{
            credentials:"include",
            method:'POST',
            body:JSON.stringify(toUpdate)
        })
        if(!result.ok)
        {
            console.log("not successfully updated");
        }
    }
        catch(error)
        {
            console.error("error updating user",error);

        }

    }
  
    return (
        <form onSubmit={update}>
            <input type='text' name='name' value={name} onChange={handleInputChange}></input>
           
            <input type='email' name='email' value={email} onChange={handleInputChange}></input>
           <select name='role' value={role} onChange={handleInputChange}>
            <option type='test' name='name' value='buyer'>buyer</option>
            <option type='test' name='name' value='seller'>seller</option>
            </select>
             <p>To change password</p>
             
            <input type='password' name='oldPassword' value={name} id='oldPassword' onChange={handleInputChange}></input>
            <input type='password' name='newPassword' value={name} id='newPassword' onChange={handleInputChange}></input>

            <button type="submit" >Change details</button>




        </form>

    )





}

export default Profile;
