    return (

        loaded ? (
            <div className="chatbox-page">


                <Chatbox messages={data?.messages || []} reload={fetchConvo} />
            </div>
        ) :
            (
                <div>
                    <h1>Loading chats</h1>
                </div>

            )

    )


}


export default ChatboxPage;
