import React, { useState, useEffect ,useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

import { GlobalContext } from './ContextWrapper';


function Navbar({link_data})
{
   const contextData = useContext(GlobalContext);
 
  
  const{cartVisible,setCartVisible,loggedIn}=contextData;


    
  const make_visible=()=>
  {
   setCartVisible(prev=>!prev);

  };
  console.log(loggedIn);
  const logOut=async ()=>
  {
    const url="http://localhost:5000/user/logout";
    try
    {
      const response=await fetch(url,{
        credentials:"include"
      }) 
      if(!rep.ok)
      {
        throw new error("error loggong out")
       
      }
      const result=await response.json();
     
      alert()

    }
    catch(err)
    {

    }

  }

    
    return(
        <div>
           <nav>
             {
                link_data.map((data)=> <Link to={data.destination} key={data.key} > {data.Label} </Link>)
             }

             {
              loggedIn&&
              (  
                <div>
                  <div>
                <button onClick={make_visible}>Cart</button>
                </div>
                  <div>
                <button onClick= {logOut}>Logout</button>
                </div>
                </div>
               )

             }
           </nav>
           

        </div>
        
    );



};

export default Navbar;
