import React, { useState, useEffect } from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import Button from './Button/Button.js';
import Form from './Form/Form.js';
import NavBar from './NavBar/NavBar.js';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
    const [message, setMessage] = useState('');
    const nav_elements = [
        //modify this to add more elements to the navbar
        { key: 'home', name: 'Home', destination: '/' },
        { key: 'test', name: 'test', destination: '/test' }

    ];

    useEffect(() => {
        fetch('http://localhost:5000/').then(response => response.json()).then(data => setMessage(data.message));
    }, []);
    const get_test_var = () => {
        fetch('http://localhost:5000/test').then(response => response.json()).then(data => setMessage(data.message));
    };

    return (
        <div>
            <NavBar elements={nav_elements} />

            <Routes>
                <Route path="/" element={get_test_var} />
            </Routes>
            <h1>{message}</h1>
        </div>
    );
}


export default App;
